    /**
     * 给定一个只包含 '(' 和 ')' 的字符串，找出最长的包含有效括号的子串的长度。
     *
     * 暴力法，超出时间限制
     *
     * @param s 给定的字符串
     * @return 最长的包含有效括号的子串的长度
     */
    private static int longestValidParentheses(String s) {
        int n = s.length();
        if (n < 2) {
            return 0;
        }

        if (n % 2 == 1) {   //若字符串长为单数
            n--;
        }

        for (int i = n; i >= 2; i -= 2) {      //子字符串长度
            if (isChildValue(i, s)) {
                return i;
            }
        }
        return 0;
    }

    private static boolean isChildValue(int n, String s) {
        for (int j = 0; j + n <= s.length(); j++) {
            if (isValid(s.substring(j, j+ n))) {
                return true;
            }
        }
        return false;
    }

    private static boolean isValid(String s) {
        if (s.equals("()")) {
            return true;
        }

        int left = 0;
        for (int i = 0; i < s.length(); i++) {
            char curr = s.charAt(i);
            if (curr == '(') {
                left++;
            } else {
                if (left == 0) {
                    return false;
                } else {
                    left--;
                }
            }
        }

        return left == 0;
    }

    /**
     * 借鉴别人的解法
     * 思路：先从左往右进行配对（"()"为一对），第二次从右往左（")("为一对），取两次配对结果的最大值
     */
    private static int longestValidParentheses2(String s) {
        int left2right = getMaxLength(s, '(');
        int right2left = getMaxLength(new StringBuilder(s).reverse().toString(), ')');
        return Math.max(left2right, right2left);
    }

    /**
     * 对字符串进行一次遍历
     *
     * @param s 给定字符串
     * @param c 配对的左半字符（从左到右时为'('，从右到左时为')'）
     * @return 最长有效子串的长度
     */
    private static int getMaxLength(String s, char c) {
        int max = 0;
        int currLength = 0;     //当前有效子串的长度
        int left = 0;           //左半字符个数
        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) == c) {
                left++;
            } else {
                if (left == 0) {    //没有与之配对的左半字符
                    currLength = 0;
                } else {
                    left--;
                    currLength += 2;
                    if (left == 0 && currLength > max) {    //注意：当和前边所有的左半字符都配对成功时才考虑更新max
                        max = currLength;
                    }
                }
            }
        }

        return max;
    }
